xicor &lt;- function(X, Y, ties = TRUE){ n &lt;- length(X) r &lt;- rank(Y[order(X)], ties.method = “random”) set.seed(42) if(ties){ l &lt;- rank(Y[order(X)], ties.method = “max”) return( 1 - nsum( abs(r[-1] - r[-n]) ) / (2sum(l(n - l))) ) } else { return( 1 - 3  sum( abs(r[-1] - r[-n]) ) / (n^2 - 1) )
} }
