from shiny import reactive, render
from shiny.express import input, ui

ui.input_action_button("action_button", "Action")  

@render.text()
@reactive.event(input.action_button)
def counter():
    return f"{input.action_button()}"

from shiny import App, ui, reactive, render

# Define questions with LaTeX formatting
questions = [
    {"question": r"What is $8 / 2 * (2 + 2)$?", "answer": "16"},
    {"question": r"What is $4 * (5 - (9 - 2))$?", "answer": "-8"},
    {"question": r"What is $10 + 2 * (-1) √ó 2^3$?", "answer": "-6"},
    {"question": r"What is $(10 + 2 * (-1) * 2^3 - 4 * (5 - (9 - 2)))$?", "answer": "2"},
    {"question": r"What is $(10+2√ó(‚àí1)√ó2^3‚àí4√ó(5‚àí(9‚àí2)))√∑8 $?", "answer": "1/4"},
    {"question": r"Ajouter des parenth√©√®ses pour que l'√©galit√© soit vraie : 3 + 5 √ó 2 = 16", "answer": "(3 + 5) √ó 2 = 16"},
    {"question": r"Ajouter des parenth√©√®ses pour que l'√©galit√© soit vraie : 10 - 2 + 4 = 4", "answer": "10 - (2 + 4) = 4"},
    {"question": r"[12 - (3 + 2^3 * (4 - 1)) / 3] * 2 - 5^2", "answer": "-19"},
    ]

# Reactive state
current_index = reactive.Value(0)
score = reactive.Value(0)
feedback_text = reactive.Value("")
show_next_button = reactive.Value(False)

# UI
app_ui = ui.page_fluid(
    ui.h2("üßÆ Math Quiz"),
    ui.output_ui("question_ui"),
    ui.input_text("user_answer", "Your answer:", ""),
    ui.input_action_button("check", "Check Answer"),
    ui.output_ui("feedback_ui"),
    ui.output_ui("next_button_ui"),
    ui.hr(),
    ui.output_ui("score_ui")
)

# Server
def server(input, output, session):
    @output
    @render.ui
    def question_ui():
        q = questions[current_index.get()]["question"]
        return ui.markdown(q)

    @output
    @render.ui
    def feedback_ui():
        return ui.markdown(feedback_text.get())

    @output
    @render.ui
    def score_ui():
        return ui.markdown(f"**‚úÖ Correct answers:** {score.get()} / {len(questions)}")

    @output
    @render.ui
    def next_button_ui():
        if show_next_button.get() and current_index.get() &lt; len(questions) - 1:
            return ui.input_action_button("next", "Next Question")
        return ui.markdown("")

    @reactive.Effect
    @reactive.event(input.check)
    def check_answer():
        user = input.user_answer().strip()
        correct = questions[current_index.get()]["answer"]

        if user == correct:
            score.set(score.get() + 1)
            feedback_text.set("‚úÖ Correct! Click 'Next Question' to continue.")
            show_next_button.set(True)
        else:
            feedback_text.set("‚ùå Try again.")
            show_next_button.set(False)

    @reactive.Effect
    @reactive.event(input.next)
    def next_question():
        if current_index.get() &lt; len(questions) - 1:
            current_index.set(current_index.get() + 1)
            feedback_text.set("")
            show_next_button.set(False)

app = App(app_ui, server)

La cotation a le m√™me objet que la notation d‚Äôune agence √† savoir √©valuer le risque encouru par un pr√™teur qui avance des fonds. le mod√®le √©conomique de la cotation est tr√®s diff√©rent :

