from shiny import App, ui, reactive, render

# Define questions with LaTeX formatting
questions = [
    {"question": r"What is $8 / 2 * (2 + 2)$?", "answer": "16"},
    {"question": r"What is $4 * (5 - (9 - 2))$?", "answer": "-8"},
    {"question": r"What is $10 + 2 * (-1) Ã— 2^3$?", "answer": "-6"},
    {"question": r"What is $(10 + 2 * (-1) * 2^3 - 4 * (5 - (9 - 2)))$?", "answer": "2"},
    {"question": r"What is $(10+2Ã—(âˆ’1)Ã—2^3âˆ’4Ã—(5âˆ’(9âˆ’2)))Ã·8 $?", "answer": "1/4"},
    {"question": r"Ajouter des parenthÃ©Ã¨ses pour que l'Ã©galitÃ© soit vraie : 3 + 5 Ã— 2 = 16", "answer": "(3 + 5) Ã— 2 = 16"},
    {"question": r"Ajouter des parenthÃ©Ã¨ses pour que l'Ã©galitÃ© soit vraie : 10 - 2 + 4 = 4", "answer": "10 - (2 + 4) = 4"},
    {"question": r"[12 - (3 + 2^3 * (4 - 1)) / 3] * 2 - 5^2", "answer": "-19"},
    ]

# Reactive state
current_index = reactive.Value(0)
score = reactive.Value(0)
feedback_text = reactive.Value("")
show_next_button = reactive.Value(False)

# UI
