    ui.output_ui("score_ui")
)

# Server
def server(input, output, session):
    @output
    @render.ui
    def question_ui():
        q = questions[current_index.get()]["question"]
        return ui.markdown(q)

    @output
    @render.ui
    def feedback_ui():
        return ui.markdown(feedback_text.get())

    @output
    @render.ui
    def score_ui():
        return ui.markdown(f"**âœ… Correct answers:** {score.get()} / {len(questions)}")

    @output
    @render.ui
    def next_button_ui():
        if show_next_button.get() and current_index.get() &lt; len(questions) - 1:
            return ui.input_action_button("next", "Next Question")
        return ui.markdown("")

    @reactive.Effect
    @reactive.event(input.check)
    def check_answer():
        user = input.user_answer().strip()
        correct = questions[current_index.get()]["answer"]

        if user == correct:
            score.set(score.get() + 1)
            feedback_text.set("âœ… Correct! Click 'Next Question' to continue.")
            show_next_button.set(True)
        else:
            feedback_text.set("âŒ Try again.")
            show_next_button.set(False)

    @reactive.Effect
    @reactive.event(input.next)
    def next_question():
        if current_index.get() &lt; len(questions) - 1:
            current_index.set(current_index.get() + 1)
            feedback_text.set("")
            show_next_button.set(False)

app = App(app_ui, server)

